require('./test.html');

var http = require('../src/index');
var app = http.createServer();

app.on('ready', function(req, res){
    console.log(app);
});

app.on('redirected', function(){
    console.log('redirect');
});

app.use(function(req, res, next){
    console.log('/:use');
    next();
});

app.use('/dist*', function(req, res, next){
    console.log('/dist:use');
    next();
});

app.load(function(req, res){
    console.log('/:load');
});

app.active(function(req, res){
    console.log('/:active');
});

app.active('/dist*', function(req, res){
    console.log('/dist:active');
    res.redirect('/most/id/2?a=1');
});

app.listen();